            .getRestaurant(user)
            .then(response => setProducts(response.data.menu))
            .catch(err => console.log(err))

        console.log(products)
    }


//
    //loadMenu()

    return (
        <>
            <ul> Lista de productos
            {
                products.map(product => {
                    return( <li> <p>{product.name}</p> {product.price}</li>)
                })
            }
            </ul>
        </>
    )
}



export default MenuList
