import { useEffect, useState } from "react";
import restaurantService from "../../services/restaurant.services";
import { useParams } from "react-router-dom";
import Basket from "./Basket";
import CreateOrder from "../../components/CreateOrder/CreateOrder";
import { Grid, Item } from "@mui/material";

const ClientView = () => {
  const [restaurant, setRestarant] = useState();

  const [isOrderSent, setIsOrderSent] = useState(false);

  const [orderForm, setOrderForm] = useState([]);

  const receiveOrder = (order) => {
    setOrderForm(order);
  };

  const orderSent = () => {
    if (isOrderSent === false) {
      setIsOrderSent(true);
    } else {
      setIsOrderSent(false);
    }
  };

  const { id, tableId } = useParams();

  useEffect(() => {
    loadRestarurant();
  }, []);

  const loadRestarurant = () => {
    restaurantService.getRestaurant("6220f66aeca2aa71f20c76d2"); //SUSTITUIR POR ID
  };

  const clearOrder = () => {
    setOrderForm({})
    //setTicket({})

    console.log('ahora order es ', props.orderForm)
  }

  return (
    <>
      <h1>Haz tu pedido</h1>

      <Grid container spacing={2}>
        <Grid item xs={8}>
          <CreateOrder 
          orderSent={orderSent}
            receiveOrder={receiveOrder}
            popino={'popino'}
          ></CreateOrder>
        </Grid>
        <Grid item xs={4}>
          <Basket
            id={id}
            isOrderSent={isOrderSent}
            orderSent={orderSent}
            orderForm={orderForm}
            setOrderForm={setOrderForm}
          ></Basket>
        </Grid>
      </Grid>
    </>
  );
};

export default ClientView;
